/** @type {import('next').NextConfig} */
const nextConfig = {
  reactStrictMode: true,
  swcMinify: true,

  // Enable static export for Cloudflare Pages and Azure Static Web Apps
  output: 'export',
  trailingSlash: true,
  images: {
    unoptimized: true
  },

  // Webpack configuration for Three.js, Babylon.js, p5.js, and Tone.js
  webpack: (config, { isServer }) => {
    // Handle Three.js and other libraries that expect window object
    if (!isServer) {
      config.resolve.fallback = {
        ...config.resolve.fallback,
        fs: false,
        path: false,
        os: false,
      };
    }

    // Optimize bundle size
    config.optimization = {
      ...config.optimization,
      splitChunks: {
        chunks: 'all',
        cacheGroups: {
          three: {
            test: /[\\/]node_modules[\\/](three|@react-three)[\\/]/,
            name: 'three',
            chunks: 'all',
          },
          babylonjs: {
            test: /[\\/]node_modules[\\/]babylonjs[\\/]/,
            name: 'babylonjs',
            chunks: 'all',
          },
          tone: {
            test: /[\\/]node_modules[\\/]tone[\\/]/,
            name: 'tone',
            chunks: 'all',
          },
        },
      },
    };

    return config;
  },

  // Experimental features for better performance
  experimental: {
    optimizeCss: true,
    webVitalsAttribution: ['CLS', 'LCP'],
  },
};

module.exports = nextConfig;

/** @type {import('next').NextConfig} */
const nextConfig = {
  experimental: {
    appDir: true,
    optimizeCss: true, // Optimize CSS for better performance
  },
  swcMinify: true,
  reactStrictMode: true,
  eslint: {
    ignoreDuringBuilds: true,
  },
  typescript: {
    ignoreBuildErrors: true,
  },
  images: {
    domains: ["your-image-domain.com"], // Add domains for image optimization
  },
  webpack: (config) => {
    // Custom Webpack configuration for art and design tools
    config.module.rules.push({
      test: /\.svg$/,
      use: ["@svgr/webpack"], // Support for SVG imports
    });
    return config;
  },
};

module.exports = nextConfig;
